function consoleBG() {
    if($(this).scrollTop() > 15) {
        $('.header_inner').css({
            'float': 'left',
            'width': '100%',
            'padding': '15px 0px 15px 0px',
            'border-bottom': 'none',

            'display': 'flex',
            'justify-content': 'space-between',
            'align-items': 'center',
        });
    } else {
        $('.header_inner').css({
            'float': 'left',
            'width': '100%',
            'padding': '15px 0px 15px 0px',
            'display': 'flex',
            'justify-content': 'space-between',
            'align-items': 'center',
            'border-bottom': '1px solid white',
        });
    }
  }
  consoleBG();
  
  $(window).scroll(function() {
    consoleBG();
  });

