function consoleBG() {
    if($(this).scrollTop() > 100) {
        $('.header_inner').css({
            'float': 'left',
            'width': '100%',
            'padding': '15px 0px 15px 0px',
            'border-bottom': '0px solid red',
            'display': 'flex',
            'justify-content': 'space-between',
            'align-items': 'center',
        });
    } else {
        $('.header_inner').css({
            'float': 'left',
            'width': '100%',
            'padding': '15px 0px 15px 0px',
            'display': 'flex',
            'justify-content': 'space-between',
            'align-items': 'center',
            'border-bottom': '1px solid white',
        });
    }
  }
  consoleBG();
  
  $(window).scroll(function() {
    consoleBG();
  });
